import React, {useState} from 'react'


function Cadastrar(){
    
    const [meta, setMeta] = useState({
        name: '',
        description: '',
        status: ''
    });

    const onChangeInput = e => setMeta({...meta, [e.target.name]: e.target.value});

    const sendMeta = async e => {
        e.preventDefault();
        console.log(meta);
    }

    return (
        <>
            <h1>Cadastrar Minha Meta</h1>
            <hr/>
            <form onSubmit={sendMeta}>
                <label>Nome</label>
                <input type="text" name="name" id="name" placeholder="Nome da meta" onChange={onChangeInput} /><br/><br/>
                <label>Descrição</label>
                <input type="text" name="description" id="description" placeholder="Descreva a meta" /><br/><br/>
                <label>Status</label>
                <input type="text" name="status" id="status" placeholder="Status da meta" /><br/><br/>
                <button type="submit">Cadastrar</button>
            </form>
        </>
    )};
        
export default Cadastrar
